import { useState } from 'react';

import './NewExpense.css';
import ExpenseForm from './ExpenseForm';

export default function NewExpense({ addExpenseHandler }) {
  // function handleExpenseDate(expenseData) {
  //   const expenseData_Form = {
  //     ...expenseData,
  //     id: Math.random().toString(),
  //   };

  //   console.log(expenseData_Form);
  // }

  let [isButtonToggle, setIsButtonToggle] = useState(false);

  function handleToggleButton (boolean) {
    setIsButtonToggle(boolean);
  }

  let inputButton = 
    <button 
      onClick={() => handleToggleButton(true)}
    >
      Add New Expense
    </button>;

  if (isButtonToggle === true) {
    inputButton = 
      <ExpenseForm 
        addExpenseHandler={addExpenseHandler}
        onClickToogle={handleToggleButton}
      />
  }

  return (
    <div className='new-expense'>
      {inputButton}
      {/* <ExpenseForm 
        addExpenseHandler={addExpenseHandler}
      /> */}
    </div>
  );
}
