import { useState } from 'react';

import './Expenses.css';
import Card from '../UI/Card';
import ExpenseItem from './ExpenseItem';
import ExpensesFilter from './ExpensesFilter';

export default function Expenses({ 
  data,
}) {
  const [filterYear, setFilterYear] = useState('2020');

  function filterChangeHandler(yearData) {
    setFilterYear(yearData);
  }

  const filteredExpenses = data.filter(expense => {
    return expense.date.getFullYear().toString() === filterYear;
  });

  return (
    <div>
      <Card className='expenses'>
        <ExpensesFilter
          selected={filterYear}
          onChangeFilter={filterChangeHandler}
        />
        {filteredExpenses.length === 0 && <p>No expenses found.</p>}
        {filteredExpenses.length > 0 && 
          filteredExpenses.map((listObj) => (
            <ExpenseItem 
              key={listObj.id}
              title={listObj.title} 
              amount={listObj.amount}
              date={listObj}>
            </ExpenseItem>
          ))
        }
        {/* {data.filter((list) => list.date.toDateString().split(' ')[3] === filterYear + "").map(lis => ( */}
        {/* {data.filter((list) => list.date.getFullYear() === filterYear).map(lis => (
          <ExpenseItem
            key={lis.id}
            title={lis.title}
            amount={lis.amount}
            date={lis}
          />
        ))} */}
      </Card>
    </div>
  );
}
